sys_variable_value.value:
(function execute(inputs, outputs) {

    var list = inputs.record.u_fertilising_schedule.toString().split(',');

    outputs.result = false;


    for (var i = 0; i < list.length; i++) {
        if (list[i] == inputs.currentMonth) {          
            outputs.result = true;
        }
    }
    
})(inputs, outputs);

