sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        setFieldStyle(newValue);
        return;
    }
    setFieldStyle(newValue);
}

function setFieldStyle(newValue) {
    var stateLabel = g_form.getLabel('status');
    switch (newValue) {
        case "dead":
            stateLabel.style.backgroundColor = "red";
            g_form.getOption("status", "dead").style.color = "red";
            break;
        case "sick":
            stateLabel.style.backgroundColor = "orange";
            g_form.getOption("status", "sick").style.color = "orange";
            break;
        case "poor":
            stateLabel.style.backgroundColor = "yellow";
            g_form.getOption("status", "poor").style.color = "yellow";
            break;
        case "good":
            stateLabel.style.backgroundColor = "green";
            g_form.getOption("status", "good").style.color = "green";
            break;
        default:
            stateLabel.style.backgroundColor = "white";
            g_form.getOption("status", "").style.color = "white";
            break;
    }
}
