sys_variable_value.value:
(function execute(inputs, outputs) {

    var jBody = JSON.parse(inputs.sBody);
    var nPrecipitation = jBody.daily.precipitation_sum; 
    outputs.rain_today = (nPrecipitation > 0);

})(inputs, outputs);

