sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  	var locations = [];
  	var plantGA = new GlideAggregate('x_snc_plants_db_my_plants');
	plantGA.addQuery('placement', 'outdoor');
	plantGA.groupBy('location');
	plantGA.query();

	while(plantGA.next()){
      locations.push({
      	sys_id: plantGA.location.toString(),
      	name: plantGA.location.name.toString(),
      	latitude: plantGA.location.latitude.toString(),
      	longitude: plantGA.location.longitude.toString()
      });
	}

    outputs.locations = locations;
})(inputs, outputs);

